DROP TYPE IF EXISTS "public";
CREATE TYPE "public" AS ENUM
('dm', 'friend', 'public');

CREATE TABLE "favor"(
    "id" INTEGER PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
    "title" TEXT NOT NULL,
    "description" TEXT,
    "creator_id" INTEGER REFERENCES "user"
(id) ON
DELETE CASCADE,
    "expiration_date" TIMESTAMPTZ,
    "publicity"
public,
    "user_location" TEXT,
    "tags" TEXT,
    "category" INTEGER,
    "limit" integer,
    "posted" TIMESTAMPTZ NOT NULL DEFAULT now
()
);

